import React, { useEffect, useState } from "react";
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button, Container
} from 'reactstrap';
import Course from './Course';
import base_url from "../api/bootapi";
import Axios from "axios";

const getAllCoursesFromServer = () => {
  Axios.get(`${base_url}/courses`).then((response) => { console.log(response) }, (error) => { console.log("MyERRORR", error) })
}


// calling api function
useEffect(() => { getAllCoursesFromServer() }, [])

const AllCourses = () => {

  const [courses, setCourses] = useState([{ title: "Java", description: "Java is Powerfull Programming Language" },
  { title: "Php", description: "Java is  Programming Language" },
  { title: "React js ", description: "It is Front End Framwork" }
  ])

  return (
    <div>
      <h1>All Courses</h1>
      <p>List Of All Courses are as Follows</p>
      {
        courses.length > 0 ? courses.map((item) => <Course key={item.id} course={item} />) : "No courses"
      }
    </div>
  )
}



export default AllCourses;
