import React, { Fragment, useState } from "react";
import { Button, Form, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import Axios from 'axios';
import base_url from '../api/bootapi';
import {toast} from 'react-toastify';

// const saveCourse = () => {
//     Axios.post(`${base_url}/courses`,)
// }

const AddCourse = () => {
    const [course, setCourse] = useState({})

    const handleForm = (e) => {
        console.log(course)
        e.preventDefault();
    }


    const saveCourse = (data) => {
        Axios.post(`${base_url}/courses`, data).
            thent((response) => {
                console.log(response);
                saveCourse(response);
                console.log("Success");
                toast.success("Course Added Successfuly..")
            }).
            catch((error) => { console.log(error) })

    }

    return (
        <Fragment>
            <h2 className="text-center my-2">Fill Course Detail</h2>

            <Form onSubmit={handleForm}>

                <FormGroup>
                    <Label for="userId">Course Id</Label>

                    <Input type="text" name="userId" id="userId"
                        onChange={(e) => { setCourse({ ...course, id: e.target.value }) }}
                        placeholder="Enter Here........"
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Title</Label>
                    <Input type="text" name="title" id="title"
                        onChange={(e) => { setCourse({ ...course, title: e.target.value }) }}
                        placeholder="Enter Title Here........"
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Description </Label>
                    <Input type="text" name="description" id="description"
                        onChange={(e) => { setCourse({ ...course, description: e.target.value }) }}
                        placeholder="Enter Description Here........"
                    />
                </FormGroup>
                <Container>
                    <Button type="submit" color="success" onClick={(e)=>{

                    }}>AddCourse</Button>
                    <Button color="warning ml-2">Clear</Button>
                </Container>

            </Form>
        </Fragment>
    )
}

export default AddCourse;
