import React, { Fragment, useState, useEffect } from "react";
import { Button, Form, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import Axios from 'axios';
import base_url from '../api/bootapi';
import { toast } from "react-toastify";

const UpdateCourse = ({ match }) => {

    // state here 
    const [values, setValues] = useState({
        id: "",
        title: "",
        description: "",
    });

    useEffect(() => {
        alert(match.params.id);
        preload(match.params.id);
    }, []);

    const preload = (id) => {
        Axios.get(`${base_url}/courses/${id}`).
            then((res) => { setValues({ ...values, id: res.data.id, title: res.data.title, description: res.data.description }) })
            .catch((error) => { console.log("eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee" + error); })

    };

    const onChangeTitle = (event) => {
        setValues({ title: event.target.value });
    };
    const onChangeDescription = (event) => {
        setValues({ description: event.target.value });
    };

    //TODO: work on it
    const onSubmit = (event) => {
        event.preventDefault();
        const obj = {
            id: values.id,
            title: values.title,
            description: values.description
        }
        console.log("oboboobobooooooooooooooooooooooooooooooboboboo"+obj);
        Axios.put(`${base_url}/courses`, obj).
            then((res) => {
                alert(res.data.title)
            })
            .catch((error) => { console.log("susussususuususuuuuuuuuuuuuussusuuussusuus" + error); })
    };

    return (
        <Fragment>
            <h2 className="text-center my-2">Update Course Detail</h2>
            <Form onSubmit={onSubmit}>
                <FormGroup>
                    <Label for="userId">Course Id</Label>
                    <Input type="text" name="userId" id="userId"
                        value={values.id}
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Title</Label>
                    <Input type="text" name="title" id="title"
                        value={values.title}
                        onChange={onChangeTitle}
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Description </Label>
                    <Input type="textarea"
                        name="description"
                        id="description"
                        value={values.description}
                        onChange={onChangeDescription}
                    />
                </FormGroup>
                <Container>
                    <Button color="success" type="submit" >UpdateCourse</Button>
                </Container>

            </Form>
        </Fragment>
    )
}

export default UpdateCourse;
