import React, { Fragment, useState, useEffect } from "react";
import { Button, Form, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import Axios from 'axios';
import base_url from '../api/bootapi';
import { toast } from "react-toastify";

const UpdateCourse = ({ match }) => {

    // state here 
    const [values, setValues] = useState({
        id: "",
        title: "",
        description: ""
    });

    const { id, title, description } = values;

    useEffect(() => {
        alert(match.params.id);
        preload(match.params.id);
    }, []);

    const preload = (id) => {
        Axios.get(`${base_url}/courses/${id}`).
            then((res) => {
                console.log("Preloooooooooooooooooooooooooooooooooooooooooooooooooooooooood" + res.data.id);
                setValues({ ...values, id: res.data.id, title: res.data.title, description: res.data.description })
            })
            .catch((error) => { console.log("eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee" + error); })

    };

    const onChangeId = (event) => {
        setValues(event.target.value);
    };

    const onChangeTitle = (event) => {
        setValues(event.target.value);
    };
    const onChangeDescription = (event) => {
        setValues(event.target.value);
    };

    //TODO: work on it
    const onSubmit = (event) => {
        event.preventDefault();
        console.log("Id:" + id + "," + "Title:" + title + "," + "des:" + description);
        Axios.put(`${base_url}/courses`, {
            id: id,
            title: title,
            description: description
        }).
            then((res) => {
                alert(res.data.title)
            })
            .catch((error) => { console.log("susussususuususuuuuuuuuuuuuussusuuussusuus" + error); })
    };

    return (
        <Fragment>
            <h2 className="text-center my-2">Update Course Detail</h2>
            <Form >

                <FormGroup>

                    <Label for="id">Course Id</Label>
                    <Input type="text"
                        name="id"
                        id="id"
                        value={id}
                        onChange={onChangeId}
                    />
                </FormGroup>


                <FormGroup>
                    <Label for="title">Course Title</Label>
                    <Input type="text"
                        name="title"
                        id="title"
                        value={title}
                        onChange={onChangeTitle}
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="description">Course Description </Label>
                    <Input type="textarea"
                        name="description"
                        id="description"
                        value={description}
                        onChange={onChangeDescription}
                    />
                </FormGroup>
                <Container>
                    <Button color="success" type="submit" onClick={onSubmit}>UpdateCourse</Button>
                </Container>

            </Form>
        </Fragment>
    )
}

export default UpdateCourse;
