import React from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button, Container, ListGroup, ListGroupItem } from 'reactstrap';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import UpdateCourse from './UpdateCourse'


const Course = ({ course }) => {


    const updateC = () => {
        <UpdateCourse data={course} />
        // alert("ID: " + course.id + " , " + " Title: " + course.title + " , " + " Description: " + course.description)
    }
    return (
        <Card>
            <CardBody>
                <CardSubtitle className="font-weight-bold">{course.title}</CardSubtitle>
                <CardText>{course.description}</CardText>
                <Container>
                    <Button color="danger">Delete</Button>
                    {/* <Button color="primary ml-2" onClick={() => { updateC() }} to="update-courses">Update</Button> */}
                    <ListGroup>
                        <Link color="primary ml-2" tag="a" to="update-course" action onClick={() => { updateC() }}>Update
                        </Link>
                    </ListGroup>
                </Container>
            </CardBody>

        </Card>
    )
}



export default Course;
