import { Button, Form, FormGroup, Label, Input, FormText, Container } from 'reactstrap';
import Axios from 'axios';
import base_url from '../api/bootapi';

const saveCourse = () => {

    Axios.post(`${base_url}/courses`,)
}


const addCourse = () => {

    const [course, setCourse] = useState({})


    return (
        <Fragment>
            <h2 className="text-center my-2">Fill Course Detail</h2>
            <Form onSubmit={handleForm}>

                <FormGroup>
                    <Label for="userId">Course Id</Label>
                    <Input type="text" name="userId" id="userId" placeholder="Enter Here........" onChange={(e)=>{setCourse({...course,id:e.target.value})}}/>
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Title</Label>
                    <Input type="text" name="title" id="title" placeholder="Enter Title Here........" onChange={handleChange} />
                </FormGroup>
                <FormGroup>
                    <Label for="userId">Course Description </Label>
                    <Input type="text" name="description" id="description" placeholder="Enter Description Here........" onChange={handleChange} />
                </FormGroup>
                <Container>
                    <Button color="success">AddCourse</Button>
                    <Button color="warning ml-2">Clear</Button>
                </Container>

            </Form>
        </Fragment >
    )
}

export default addCourse;
