import React from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button, Container } from 'reactstrap';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import UpdateCourse from './UpdateCourse'




const Course = ({ course }) => {


    const updateC = () => {
        <UpdateCourse data={course} />
        // alert("ID: " + c.id + " , " + " Title: " + c.title + " , " + " Description: " + c.description)
    }
    return (
        <Card>
            <CardBody>
                <CardSubtitle className="font-weight-bold">{course.title}</CardSubtitle>
                <CardText>{course.description}</CardText>
                <Container>
                    <Button color="danger">Delete</Button>
                    <Button color="primary ml-2" onClick={() => { updateC() }}>Update</Button>
                </Container>
            </CardBody>

        </Card>
    )
}



export default Course;
